Invalid objects - Count.plsql_block:
Declare
	fCount Number := 0;
Begin
	DBMS_OUTPUT.put_line('');
	For x In (SELECT owner, COUNT(*) As Count FROM dba_objects WHERE status <> 'VALID' GROUP BY owner) Loop
		DBMS_OUTPUT.put_line('Invalid objects of user ' || x.owner || ': ' || x.count);
		fCount := fCount + x.count;
	End Loop;
	If fCount = 0 Then
		DBMS_OUTPUT.put_line('No invalid objects.');
	End If;
End;
